package newpackage;

public class Arth {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 55;
		int b = 75;
		System.out.println(a+b);
		///System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);
		
		int x = 5;
		int y;
		
		y=x;
		System.out.println(y);
		
		y+=x;
		System.out.println(y);
		
		y-=x;
		System.out.println(y);
		
		y*=x;
		System.out.println(y);
		
		y/=x;
		System.out.println(y);
		
		y%=x;
		System.out.println(y);
		
		
	}

}
